import React, { useState, useEffect } from 'react';
import { verifyUser, getUser, removeUser } from '../data/repository';

function SignIn(props) {
  const [loginDetails, setLoginDetails] = useState({ email: '', password: '' });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginError, setLoginError] = useState('');

  useEffect(() => {
    const user = getUser();
    if (user) {
      setIsLoggedIn(true);
      loginUser(user); // Use destructured prop
    }
  }, [loginUser]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setLoginDetails({ ...loginDetails, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const user = await verifyUser(loginDetails.email, loginDetails.password);
      if (user) {
        setIsLoggedIn(true);
        setLoginError('');
        props.loginUser(user); // Update parent component's user state
      } else {
        setLoginError('Invalid email or password.');
      }
    } catch (error) {
      setLoginError('An error occurred during login.');
    }
  };

  const handleLogout = () => {
    removeUser();
    setIsLoggedIn(false);
    props.loginUser(null); // Update parent component's user state
  };

  if (isLoggedIn) {
    return (
      <div className="loggedin-container">
        <div className="loggedin-message">
          <h2>Welcome back!</h2>
          <p>You are now logged in.</p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="bg-green-500 text-white py-4">
        <h1 className="text-3xl font-semibold text-center">Sign In</h1>
      </div>
      <div className="signin-container">
        <div className="signin-background"></div>
        <form onSubmit={handleSubmit} className="signin-form">
          <input
            type="email"
            name="email"
            placeholder="Email"
            required
            onChange={handleInputChange}
            className="signin-input"
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            required
            onChange={handleInputChange}
            className="signin-input"
          />
          <button type="submit" className="signin-button">Sign In</button>
          {loginError && <div className="error-message">{loginError}</div>}
        </form>
      </div>
    </div>
  );
}

export default SignIn;
